css:
.print-format {
    padding: 0.20in !important;
    font-size: 14px !important;
    margin: 2rem !important;
}

@media print {
    @page {
        margin: 0in !important;
        padding: 2rem !important; /* Adjusted for print padding */
    }
} 

.column {
    float: left;
    width: 50%;
}

.column-1 {
    float: left;
    width: 25%;
}

/* Clear floats after the columns */
.row:after {
    content: "";
    display: table;
    clear: both;
}

.row {
    margin-right: 0px !important;
    margin-left: 0px !important; 
}

.print-table {
    width: 100%;
    color: black;
    border-collapse: collapse; /* Ensure borders are collapsed */
}

.print-format td, 
.print-format th {
    padding: 0.5px !important; /* Remove padding */
    margin: 0 !important; /* Remove margin */
    vertical-align: middle !important;
    line-height: 1; /* Set line height to remove extra space */
}

.print-table .number {
    width: 5%;
    text-align: center;
}

.print-table .checkbox {
    width: 5%;
    text-align: center;
}

/* Checkbox styling */
.checkbox-box {
    display: inline-block;
    width: 18px; /* Width of the checkbox */
    height: 18px; /* Height of the checkbox */
    border: 1px solid rgba(195, 213, 234, 1); /* Border color */
    background-color: white; /* Background color */
    position: relative;
    cursor: pointer; /* Cursor change for interaction */
}

.checkbox-box.checked {
    background-color: white; /* Background color when checked */
}

.checkmark {
    display: none; /* Hide checkmark by default */
    position: absolute;
    left: 2px; /* Adjusted position for better centering */
    top: 0; /* Positioning for checkmark */
    font-size: 16px; /* Size of the checkmark */
    color: black; /* Color of the checkmark */
}

.checkbox-box.checked .checkmark {
    display: block; /* Show checkmark when checked */
}

.print-format {
    padding: 0.2in !important;
