@media print {
    html {
        counter-reset: pageNumber; /* Reset the page number counter */
    }

    body {
        counter-increment: pageNumber; /* Increment the counter per page */
    }

    .page-number::before {
        content: "Page: " counter(pageNumber); /* Display the page number */
    }

    @page {
        counter-increment: pageNumber; /* Ensure increment on each page */
    }
}
