}

ol {
    counter-reset: item;
}

li {
    display: block;
}

li:before {
    content: counters(item, ".") " ";
    counter-increment: item;
}

/* Custom alphabetical ordered list */
ol.custom-alpha {
    counter-reset: list; 
    margin-bottom: 0; 
}

ol.custom-alpha li {
    counter-increment: list; 
    margin-bottom: 10px;
}

ol.custom-alpha li:last-child {
    margin-bottom: 0;
}

ol.custom-alpha li::before {
    content: counter(list, lower-alpha) "." " "; 
    margin-right: 5px;
}

/* Custom Roman numeral ordered list */
ol.custom-roman {
    counter-reset: list;
    margin-bottom: 0;
}

ol.custom-roman li {
    counter-increment: list;
    margin-bottom: 10px;
}

ol.custom-roman li:last-child {
    margin-bottom: 0;
}

ol.custom-roman li::before {
    content: counter(list, lower-roman) "." " "; 
    margin-right: 5px;
}

